package test.day2;

public class Ex6Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 대입 연산자
		int a=5;
		a+=3; //a=a+3
		System.out.println(a); //8
		a-=5; //a=a-5
		System.out.println(a); //3
		a*=6; //a=a*6
		System.out.println(a); //18
		a/=5; //a=a/3
		System.out.println(a); //3
		a%=2; //a=a%2
		System.out.println(a); //1
		
		
	}

}
